import React, { useEffect, useState } from 'react';
import './App.css';
// import component1 from './components/component1';
// import component2 from './components/component2';
// import component3 from './components/component3';
// import component4 from './components/component4';

// const componentNames = {
//   component1,
//   component2,
//   component3,
//   component4
// }

function App() {


  const requireModule = require.context('./components', false, /\.js$/);

  const modules = {};

  requireModule.keys().forEach(fileName => {
  const moduleName = fileName.replace(/(\.\/|\.js)/g, '');
  console,log("mm", modules);
  modules[moduleName] = requireModule(fileName).default;
  });

  console.log(modules);


  return (
    <>
      <h1 style={{ textAlign: "center" }} >Dynamic Component</h1>
      <h3 style={{ textAlign: "center" }} >This is how we can show dynamic components</h3>

       <div className="App">
        <br />
        {
          Object.keys(modules).map(moduleName =>
            <div class="card">
              <div class="container">
                <h4><b>{moduleName}</b></h4>
                <p>Dynamic Component</p>
              </div>
            </div>
          )
        }
      </div>


    </>
  );
}

export default App;
